#include <string.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <sys/mman.h>

#define PAGE_SIZE 4096

int main() {
    int pipefd[2];
    char *buf;
    char *addr;

    // Create a pipe
    if (pipe(pipefd) == -1) {
        perror("pipe");
        exit(1);
    }

    // Map the pipe buffer
    buf = mmap(NULL, PAGE_SIZE, PROT_READ | PROT_WRITE, MAP_SHARED, pipefd[0], 0);
    if (buf == MAP_FAILED) {
        perror("mmap");
        exit(1);
    }

    // Write to the pipe
